import '../css/Home.css'
import Depto1 from "../Img/Departamento1.jpg";
import Depto2 from "../Img/Departamento2.jpg";
import Depto3 from "../Img/Departamento3.jpg";
import Productos from './Productos';
const Home = () => {

    const [productos, setProductos] = useState([]);



    const carrito = () => {

        alert("Producto agregado al carrito");

        const producto = {
            nombre: 'Departamento 1',
            precio: 2500,
            descripcion: 'Ideal para todo trabajo aer贸bico. - Con sales de rehidrataci贸n.',
            img: {Depto1}
        }

        localStorage.setItem('producto', JSON.stringify(producto));
        localStorage.setItem('datos', 'hola gente, soy datos'); /* no hace nada, es un ejemplo */

    }
    return (
        <div class="container">
            {productos.map((producto)=>(
    <div class="card">
        <figure>
            <img src={producto.img} alt="logo" />
        </figure>
        <div class="contenido">
            <h3>{producto.nombre}</h3>
            <p>{producto.descripcion}</p>
            <button onClick={carrito}>Comprar</button>
        </div>
    </div>
    ))}
</div> 
    )
}
export default Home;
