import { useNavigate } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import axios from 'axios';
import { useEffect, useState } from 'react';
import Swal from 'sweetalert2';

function Usuarios() {

    const [usuarios, setUsuarios] = useState([]);
    const navigate = useNavigate(); // Inicializar el hook

    useEffect(() => {

        try {
            const obtenerUsuarios = async () => {
                const respuesta = await axios.get('http://localhost:9000/user/');
                setProductos(respuesta.data);

                console.log(respuesta);
                console.log(respuesta.status);

            }

            obtenerProductos();

            console.log(usuarios);

        } catch (error) {
            console.error('Error al obtener los usuarios:', error);
        }

    }, []);

    const eliminarUsuario = async (id) => {

        console.log(id);
        
        try {
            const respuesta = await axios.delete(`http://localhost:9000/user/${id}`);
            setUsuarios(usuarios.filter(usuario => usuario._id !== id));
            console.log(respuesta);

        } catch (error) {
            console.error('Error al eliminar el usuario:', error);
        }
    }   

    const confirmarEliminación = (id) => {

        console.log(id);
        
        Swal.fire({
            title: "Estás seguro?",
            text: "Esto no se puede revertir!",
            icon: "warning",
            showCancelButton: true,
            confirmButtonColor: "#3085d6",
            cancelButtonColor: "#d33",
            confirmButtonText: "Yes, delete it!"
        }).then((result) => {
            if (result.isConfirmed) {
                eliminarUsuario(id);
                Swal.fire({
                    title: "Usuario Eliminado!",
                    text: "El archivo se ha eliminado.",
                    icon: "success"
                });
            }
        });

        
    }

    const mostrarFormularioEdicion = (id) => {
        navigate(`/editar/${id}`);
    }

    return (
        <>
            <h1 className='container mt-5 text-center'>
                Clientes Activos
            </h1>
            <div className='container mt-5 text-center'>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>Nombre</th>
                            <th>Email</th>
                            <th>Action</th>
                        </tr>
                    </thead>
